import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { register } from '../api/auth';

export default function Register() {
  const [form, setForm] = useState({
    first_name: '', last_name: '', email: '', phone: '', password: '', role: 'Therapist'
  });
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await register(form);
      navigate('/login');
    } catch (err) {
      alert(err.response?.data?.msg || 'Registration failed');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 300, margin: 'auto' }}>
      <h2>Register</h2>
      {['first_name','last_name','email','phone','password'].map((fld) => (
        <div key={fld}>
          <label>{fld.replace('_',' ')}</label><br />
          <input
            name={fld}
            type={fld==='password'?'password':'text'}
            value={form[fld]}
            onChange={handleChange}
            required
          />
        </div>
      ))}
      <div>
        <label>Role</label><br />
        <select name="role" value={form.role} onChange={handleChange}>
          {['Therapist','Practice Manager','Billing Staff','Admin Staff','Executive','HR'].map(r => (
            <option key={r} value={r}>{r}</option>
          ))}
        </select>
      </div>
      <button type="submit">Register</button>
    </form>
  );
}
